INSERT INTO Food(id, name, description, image) values
(null,'Ốc Hương Xào Bơ Tỏi','Bạn là “fan cuồng” của món ốc hương nhưng ngại dùng bên ngoài vì lo sợ chất lượng món ăn không đảm bảo hoặc chưa hợp vệ sinh. Hãy cùng tham khảo ngay cách làm ốc hương xào bơ tỏi được CET hướng dẫn nhé, đảm bảo ăn là ghiền!','https://www.cet.edu.vn/wp-content/uploads/2022/09/cach-lam-oc-huong-xao-bo-toi.jpg'),
(null,'Mực Xào Dứa Ngọt Thơm','Mực xào dứa là món ăn quen thuộc của nhiều gia đình Việt. Vị dai giòn đặc trưng của mực kết hợp với vị ngọt tự nhiên từ dứa đã tạo nên một món ăn vô cùng hấp dẫn. Nếu chưa hài lòng với cách làm mực xào dứa hiện tại, bạn hãy thử ngay các bước chế biến được hướng dẫn ngay dưới đây nhé!','https://www.cet.edu.vn/wp-content/uploads/2022/04/muc-xao-dua.jpg'),
(null,'Cải Thảo Xào','Với 4 cách xào cải thảo vừa nhanh vừa lạ miệng sau đây, người nội trợ sẽ chẳng còn phải đau đầu suy nghĩ về việc chọn món cho bữa cơm nữa.','https://www.cet.edu.vn/wp-content/uploads/2019/01/cai-thao-xao-thit-bo.jpg'),
(null,'Ngan Xào Sả Ớt, Ngan Xào Lăn','Món ngan xào sả ớt và ngan xào lăn không chỉ hấp dẫn, kích thích vị giác bằng hương thơm nức mũi mà còn là những món ăn thích hợp cho mọi bữa cơm. Đặc biệt trên bàn nhậu mà có sự xuất hiện của hai món này thì tuyệt vời “hết sảy”. Cùng Cet.edu.vn tìm hiểu cách làm ngan xào sả ớt và ngan xào lăn nhé, không quá khó đâu!','https://www.cet.edu.vn/wp-content/uploads/2019/03/ngan-xao-sa-ot-1.jpg'),
(null,'Thịt Gà Xào Lăn, Xào Sả Ớt, Xào Nấm','Thịt gà là một trong những nguyên liệu vô cùng quen thuộc mang đến những hương vị độc đáo cho bữa cơm Việt. Trong bài viết hôm nay, Cet.edu.vn sẽ giới thiệu đến các bạn cách làm các món thịt gà xào như thịt gà xào lăn, thịt gà xào sả ớt và thịt gà xào nấm vừa ngon miệng lại vừa đầy đủ dưỡng chất.','https://www.cet.edu.vn/wp-content/uploads/2019/02/thit-ga-xao-sa-ot.jpg'),
(null,'Lươn Xào Sả Ớt','Mùi thơm của lá lốt, vị cay cay của sả ớt quyện với thịt lươn dai ngọt tạo nên một món ăn vô cùng hấp dẫn. Trong bài viết hôm nay, Cet.edu.vn sẽ hướng dẫn các bạn cách làm món lươn xào sả ớt chuẩn vị cho bữa cơm cuối tuần thêm đậm đà nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/12/luon-xao-xa-ot.jpg'),
(null,'Hến Xào','Thịt hến với vị ngọt tự nhiên và hàm lượng dinh dưỡng cao thường được chị em sử dụng chế biến thành nhiều món ăn hấp dẫn như hến xào miến, hến xào sả ớt, hến xào xúc bánh đa… Trong bài viết sau đây, Cet.edu.vn sẽ hướng dẫn cho bạn cách làm các món hến xào ngon chuẩn vị miền Trung nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/04/hen-xao-sa-ot.jpg'),
(null,'Thịt Bò Xào Hành Tây','Miếng thịt bò mềm đậm đà, hành tây giòn ngon chắc chắn không chỉ là món ăn khiến các thành viên hài lòng mà còn cung cấp đủ dinh dưỡng cho cả nhà sau một ngày dài làm việc vất vả. Hôm nay, hãy đến với cách làm thịt bò xào hành tây do các đầu bếp của CET chia sẻ nhé.','https://www.cet.edu.vn/wp-content/uploads/2019/01/thit-bo-xao-hanh-tay.jpg'),
(null,'Ếch Xào Sả Ớt, Sa Tế','Nếu bạn đã “chán chê” với các món thịt heo, bò, gà thì hôm nay chúng ta sẽ cùng nhau đổi vị với 2 món ăn rất hấp dẫn là ếch xào sả ớt và ếch xào sa tế đậm đà hương vị miền quê. Với sự hướng dẫn từ các đầu bếp của Cet.edu.vn, bạn sẽ học được ngay cách làm ếch xào Sả ớt và ếch xào sa tế siêu ngon chỉ với vài bước chế biến đơn giản.','https://www.cet.edu.vn/wp-content/uploads/2018/04/ech-xao-sa-te.jpg'),
(null,'Pad Thái','Pad Thái là món ăn nổi tiếng của xứ sở Chùa Vàng và được rất nhiều du khách quốc tế biết đến. Vậy bạn có biết Pad Thái gồm những nguyên liệu gì và được chế biến thế nào không? Trong bài viết sau đây, hãy cùng Cet.edu.vn tìm hiểu xem Pad Thái là gì và cách làm món ăn này thế nào nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/06/pad-thai-la-mon-an-noi-tieng.jpg'),
(null,'Ốc Xào Sả ớt và Ốc Xào Măng','Những món ăn được chế biến từ ốc là món khoái khẩu của rất nhiều người. Nếu bạn cũng yêu thích ốc thì đừng bỏ qua cách làm ốc xào sả ớt và ốc xào măng ngon tuyệt cú mèo dưới đây. Chắc chắn hai món này sẽ khiến bạn mê mẩn ngay từ lần nếm thử đầu tiên.','https://www.cet.edu.vn/wp-content/uploads/2019/06/oc-xao-sa-ot-cay.jpg'),
(null,'Mướp Đắng (Khổ Qua) Xào Trứng','Khổ qua (hay mướp đắng) là một trong những nguyên liệu nấu ăn giàu dinh dưỡng và rất tốt cho sức khỏe. Các đầu bếp dùng khổ qua để chế biến thành nhiều món ăn ngon hấp dẫn, nhưng phổ biến nhất phải kể đến món mướp đắng xào trứng là món ăn yêu thích của rất nhiều gia đình. Vì vậy, Cet.edu.vn sẽ mách bạn cách làm món mướp đắng (khổ qua) xào trứng ngon, đơn giản mà vẫn giữ được các chất dinh dưỡng của món ăn nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/09/kho-qua-xao-trung.jpg'),
(null,'Thịt Xào Mắm Ruốc','Với cách làm món thịt xào mắm ruốc mà Cet.edu.vn hướng dẫn sau đây, sau khi làm xong, bạn chỉ cần bỏ mắm ruốc vào ngăn mát tủ lạnh. Cho dù hũ mắm ruốc bạn để hàng tháng mà vẫn không làm ảnh hưởng tới mùi vị thơm ngon.','https://www.cet.edu.vn/wp-content/uploads/2018/11/thit-xao-mam-ruoc.jpg'),
(null,'Miến Xào','Hôm nay, Cet.edu.vn sẽ hướng dẫn bạn 3 công thức cách làm miến xào: thập cẩm, lòng gà và hải sản quen thuộc nhưng cũng không kém phần hấp dẫn. Đây đều là những món mà bạn có thể thực hiện nhanh chóng khi không có nhiều thời gian nấu nướng cũng như dùng để thay đổi cho bữa ăn trở nên đa dạng, phong phú hơn.','https://www.cet.edu.vn/wp-content/uploads/2018/04/mi-xao-thap-cam.jpg'),
(null,'Ếch Xào Lăn','Bạn đã nhiều lần tìm cách làm món ếch xào lăn thơm ngon chuẩn vị như ở các nhà hàng. Tuy nhiên, bạn lại không thành công. Bạn có biết nguyên nhân không? Đó là do bạn chưa nắm được bí quyết. Đây cũng là lý do hôm nay, Cet.edu.vn gửi đến bạn bài viết này.','https://www.cet.edu.vn/wp-content/uploads/2018/08/ech-xao-lan.jpg'),
(null,'Xào Lòng Lợn với Dưa Cải Chua','Thời tiết cuối năm se se lạnh được thưởng thức món cách làm lòng lợn xào dưa cải chua dai dai, giòn giòn, đậm đà hao cơm phải biết.','https://www.cet.edu.vn/wp-content/uploads/2018/11/long-lon-xao-nghe.jpg'),
(null,'Xào Cà Tím','','https://www.cet.edu.vn/wp-content/uploads/2018/09/ca-tim-1.jpg'),
(null,'Rau Muống Xào Tỏi','Rau muống là thực phẩm cung cấp rất nhiều chất dinh dưỡng tốt cho sức khỏe. Đặc biệt, trong những ngày nắng nóng thì đây là thực phẩm giải nhiệt rất hiệu quả. Từ rau muống, bạn có thể chế biến thành nhiều món ngon khác nhau như luộc, xào, nấu canh hến, ngao… Bài viết hôm nay, CET sẽ hướng dẫn bạn cách làm món rau muống xào tỏi ngon miệng. Cùng theo dõi và bắt tay vào thực hiện ngay nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/03/rau-muong-xao-toi.jpg'),
(null,'Mì Ý (Spaghetti) Sốt Bò Bằm Cà Chua','Mì Ý là một ăn đặc trưng của ẩm thực phương Tây nhưng hiện nay đã rất phổ biến và được yêu thích tại Việt Nam. Công thức của món ăn không rườm rà như bạn vẫn hay nghĩ. Hãy học ngay cách làm mì Ý dưới đây để tự tay chuẩn bị cho gia đình thưởng thức nhé!','https://www.cet.edu.vn/wp-content/uploads/2021/03/cach-lam-mi-y.jpg'),
(null,'Bạch Tuột Xào','Bạch tuột có vị giòn giòn, tươi ngọt có thể chế biến thành nhiều món ăn hấp dẫn, đậm đà. Do đó, đây là nguyên liệu được rất nhiều các chị em nội trợ yêu thích. Trong bài viết sau đây, hãy cùng Cet.edu.vn khám phá bí quyết làm các món bạch tuột xào để đổi vị cho cả nhà nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/04/bach-tuot-xao-sa-te.jpg'),
(null,'Mực Xào Sa Tế','Các món mực xào luôn có sức hấp dẫn nhất định và ăn với cơm trắng, canh nóng thì không gì còn tuyệt vời hơn. Ngoài các món mực xào hành tây, mực xào chua ngọt… đã quá quen thuộc, hôm nay với cách làm mực xào sa tế mà Cet.edu.vn sẽ hướng dẫn bạn sau đây thì cả nhà bạn sẽ vô cùng thích thú với món mực siêu ngon này.','https://www.cet.edu.vn/wp-content/uploads/2018/04/muc-xao-sa-te.jpg'),
(null,'Mực Xào Chua Ngọt','','https://www.cet.edu.vn/wp-content/uploads/2021/01/muc-xao-chua-ngot.jpg'),
(null,'Thịt Trâu Xào Lá Lốt Và Rau Muống','Thịt trâu được xem là một nguyên liệu chế biến món ăn giàu dinh dưỡng và rất được nhiều người yêu thích. Nếu bạn đang có sẵn thịt trâu trong bếp mà vẫn chưa biết thịt trâu xào gì ngon thì cách làm thịt trâu xào lá lốt và xào rau muống thơm lừng hấp dẫn sau đây sẽ là hai gợi ý lý tưởng để làm phong phú bữa cơm tối gia đình. Cùng tìm hiểu các món ngon từ thịt trâu với Cet.edu.vn nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/10/thit-trau-xao-la-lot.jpg'),
(null,'Hột Vịt Lộn Xào Me, Sa Tế và Tỏi','Trứng vịt lộn là loại thực phẩm được nhiều người yêu thích bởi hương vị mới lạ, thơm ngon và bổ dưỡng của nó. Với nguyên liệu này, bạn có thể biến tấu thành nhiều món ăn hấp dẫn khác nhau như hột vịt lộn xào me, hột vịt lộn xào sa tế, hột vịt lộn xào tỏi… Hãy cùng CET vào bếp và thực hiện những món ăn hấp dẫn từ nguyên liệu này nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/03/hot-vit-lon-xao-me-hap-dan.jpg'),
(null,'Măng Tây Xào Thịt Bò','Những ngày bận bịu công việc mà vẫn muốn tự tay làm cơm cho gia đình thì bạn nên chọn món ăn gì? Măng tây xào thịt bò là một gợi ý phù hợp, vừa chế biến đơn giản vừa cung cấp đầy đủ chất dinh dưỡng cho cả nhà. Vậy để hiểu rõ hơn về món ăn này cũng như cách chế biến thì bạn đừng bỏ qua bài viết sau nhé!','https://www.cet.edu.vn/wp-content/uploads/2020/12/cropped-mang-tay.png'),
(null,'Cua Rang Me','Cua là một trong những loại hải sản được rất nhiều người yêu thích. Loại thực phẩm này không chỉ dễ tìm mua, có cách chế biến đa dạng mà còn chứa rất nhiều dưỡng chất có lợi cho sức khỏe. Nếu là một “tín đồ” của cua, bạn nhất định đừng bỏ qua bài chia sẻ cách làm của rang me sau đây đến từ CET nhé!','https://www.cet.edu.vn/wp-content/uploads/2020/11/cropped-cua-bien-rang-me.jpg'),
(null,'Thịt Dê Xào Sả Ớt','Thịt dê từ lâu được nhiều người yêu thích bởi sự bổ dưỡng cũng như hương vị hấp dẫn. Các món ăn làm từ thịt dê cũng rất phổ biến với hội sành ăn, mà nổi bật trong số đó là thịt dê xào sả ớt. Chút cay của ớt hòa cùng mùi thơm của sả và thịt dê sau khi xào sẽ sẵn sàng “đốn tim” bất kỳ ai ngay từ lần đầu thưởng thức. Ngay bây giờ, hãy cùng CET khám phá công thức chế biến thịt dê xào sả ớt đơn giản nhưng “ngon khó cưỡng” trong bài viết sau đây nhé!','https://www.cet.edu.vn/wp-content/uploads/2020/08/cropped-Thit-de-xao-sa-ot.jpg'),
(null,'Miến Xào Cua','Những ngày cuối tuần, thay vì nấu bữa cơm như thông thường, bạn','https://www.cet.edu.vn/wp-content/uploads/2019/07/mien-xao-cua.jpg'),
(null,'Gân Bò Xào Giòn','Với cách làm gân bò xào sa tế và gân bò xào sả ớt vô cùng hấp dẫn này, bạn sẽ dễ dàng biến tấu cho những bữa ăn gia đình thêm phong phú, lôi cuốn. Rất nhiều người đã truyền tai nhau công thức món ăn này và áp dụng rồi, bạn còn chờ gì nữa mà không vào bếp ngay cùng CET nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/07/gan-bo-xao-sa-te.jpg'),
(null,'Khoai Tây Xào Cà Chua','Thử một lần thay đổi khẩu vị cho cả nhà với cách làm khoai tây xào cà chua thơm ngon dưới đây để có bữa cơm thanh đạm ngon lành nhé. Món ăn này rất thích hợp cho những người đang có xu hướng ăn chay để bảo vệ sức khỏe. Tuy là rau củ nhưng hàm lượng dinh dưỡng của khoai tây và cà chua rất cần thiết cho cơ thể của chúng ta.','https://www.cet.edu.vn/wp-content/uploads/2019/06/khoai-tay-xao-ca-chua.jpg'),
(null,'Thịt Bò Xào Mì Tôm','Thịt bò xào mì tôm là một trong những món ăn sáng khá dễ làm, vừa nhanh lại vừa thơm ngon, bổ dưỡng nên được rất nhiều người yêu thích. Nếu chưa biết cách làm thịt bò xào mì sao cho hấp dẫn và chuẩn vị nhất, đừng bỏ qua bài viết này để có ngay công thức nấu ăn siêu tiện lợi “bỏ túi” nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/06/mi-xao-bo.jpg'),
(null,'Vịt Xào Dứa và Vịt Xào Măng','Vịt xào dứa và vịt xào măng vừa có vị ngọt mềm của vịt xen lẫn vị chua nhẹ của măng và dứa mang đến cho người ăn những hương vị độc đáo và mới lạ. Hãy cùng Cet.edu.vn tham khảo ngay cách làm 2 món ngon từ vịt này nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/06/vit-xao-dua-chua-ngot-la-mieng.jpg'),
(null,'Thịt Bò Xào Bông Thiên Lý','Thịt bò mềm dai kết hợp với bông thiên lý giòn ngọt tạo nên một hương vị vừa mộc mạc, dân dã lại rất bổ dưỡng có thể chinh phục bất kỳ thực khách khó tính nào. Vì thế, hãy cùng CET tìm hiểu ngay cách làm món xào trong bài viết sau đây nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/05/bo-xao-thien-ly.jpg'),
(null,'Ốc Móng Tay Xào Rau Muống Mì Gói','Ốc móng tay xào rau muống mì gói là món ăn luôn làm xiêu lòng bất kỳ thực khách nào vì quá đỗi hấp dẫn. Cách làm món ăn này cũng rất đơn giản và không tốn nhiều thời gian, vì thế bạn có thể thực hiện để chiêu đãi cả nhà thân yêu của mình.','https://www.cet.edu.vn/wp-content/uploads/2019/05/oc-mong-tay-xao-rau-muong-mi-goi.jpg'),
(null,'Bún Xào Chay','Ăn chay hiện đang là xu hướng rất được yêu thích, không riêng gì Phật tử mà nó còn phổ biển trong bữa ăn của nhiều gia đình Việt. Một trong những món chay đơn giản, dễ làm chính và có thể dùng cho cả 3 bữa ăn chính là bún xào chay. Trong bài viết sau đây, hãy cùng Cet.edu.vn học hỏi ngay cách xào bún xào chay siêu ngon nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/05/bun-xao-chay-thanh-dam-1.jpg'),
(null,'Mầm Đá Xào','Bạn đã từng nghe qua câu chuyện Trạng Quỳnh dùng mầm đá để dâng Vua? Bạn cứ ngỡ đó là một món ăn chỉ có trong cổ tích? Nhưng không phải thế, rau mầm đá là một đặc sản rất hiếm có của vùng Sapa và được chế biến thành nhiều món ăn hấp dẫn, lạ miệng. Sau đây, cùng CET học ngay cách làm mầm đá xào đơn giản nhé!','https://www.cet.edu.vn/wp-content/uploads/2019/04/mam-da-rat-nhanh-chin.jpg'),
(null,'Ốc Bươu Xào','Các món ốc bươu xào luôn luôn khiến thực khách xuýt xoa bởi hương thơm lừng cùng vị mặn ngọt chua cay hài hòa. Tuy nhiên, làm thế nào để chế biến nên hương vị khó quên đấy thì không phải ai cũng làm được.','https://www.cet.edu.vn/wp-content/uploads/2019/03/oc-buou-xao-kieu-thai.jpg'),
(null,'Thịt Bò Xào Nấm','Thịt bò xào nấm là món ăn thơm ngon, bổ dưỡng và rất dễ chế biến. Sự kết hợp giữa vị ngọt tự nhiên của nấm và thịt bò khiến món ăn trở nên cực kì cuốn hút. Thậm chí, cách làm thịt bò xào nấm có thể biến tấu với nhiều loại nấm khác nhau tạo ra hương vị đa dạng, mới lạ. Hãy cùng tìm hiểu cách làm thịt bò mềm trong công thức thịt bò xào nấm kim châm, nấm rơm và nấm hương được chia sẻ ngay sau đây.','https://www.cet.edu.vn/wp-content/uploads/2019/01/thit-bo-xao-nam-kim-cham.jpg'),
(null,'Lòng Xào Nghệ','Lòng heo xào nghệ là món ăn dân dã nhưng hết sức thơm ngon và quen thuộc trong bữa cơm của người miền Trung. Với cách làm món lòng xào nghệ và cách làm bún lòng xào nghệ chuẩn vị sau đây, chắc chắn sẽ khiến cả gia đình bạn vô cùng thích thú.','https://www.cet.edu.vn/wp-content/uploads/2018/12/long-non-xao-nghe.jpg'),
(null,'Thịt Chó Xào Lăn','Thịt chó xào lăn là một món ngon “danh bất hư truyền” mà các dân nhậu cực kỳ yêu thích. Tuy nhiên, mỗi vùng miền lại có cách chế biến thịt chó riêng để làm nổi bật hương vị thơm ngon của thực phẩm này. Hôm nay, Cet.edu.vn sẽ hướng dẫn bạn cách làm thịt chó xào lăn ngon như nhà hàng để bạn đãi khách đến chơi nhà dịp cuối tuần nhé.','https://www.cet.edu.vn/wp-content/uploads/2018/12/cho-xao-lan-chuan-vi-mien-nam.jpg'),
(null,'Ốc Giác Xào Rau Muống, Sa Tế, Bơ Tỏi','Các món ăn từ ốc giác như ốc giác xào rau muống, ốc giác xào sa tế, ốc giác xào bơ tỏi luôn nhận được sự yêu thích của nhiều người. Và hôm nay, CET sẽ hướng dẫn bạn cách làm các món ăn này siêu ngon và đơn giản để mỗi khi phát thèm không cần phải vất vả tìm kiếm địa chỉ bán ngon nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/11/oc-giac-xao-rau-muong.jpg'),
(null,'Thịt Ngỗng Xào Lăn và Sả Ớt','Với 2 cách làm món thịt ngỗng xào lăn và cách làm món thịt ngỗng xào sả ớt sau đây, bữa tối của cả gia đình bạn vừa ngon vừa đầy đủ chất dinh dưỡng.','https://www.cet.edu.vn/wp-content/uploads/2018/11/lam-ngong-xao-lan.jpg'),
(null,'Gà Xào Bắp Cải Phô Mai','Món gà xào bắp cải phô mai ở Hàn Quốc có tên gọi là “dakgalbi”. Nếu có lỡ trót nghiền món ăn hấp dẫn này của xứ sở kim chi thì hãy xem ngay cách làm gà xào bắp cải phô mai mà Cet.edu.vn chia sẻ trong bài viết sau đây. Vị thơm mềm của thịt gà trộn cùng bắp cải hòa quyện với phô mai béo ngậy tạo nên món gà xào phô mai lôi cuốn ngất ngây.','https://www.cet.edu.vn/wp-content/uploads/2018/10/ga-xao-bap-cai-pho-mai.jpg'),
(null,'Bún Xào Singapore','Bún xào Singrapore không chỉ có cách làm đơn giản, không tốn quá nhiều thời gian mà hương vị của món ăn còn thơm ngon và đậm đà. Hãy cùng CET bắt tay vào thực hiện ngay món ăn này cho bữa sáng của cả gia đình nhé!','https://www.cet.edu.vn/wp-content/uploads/2018/10/thanh-pham-bun-xao-singapore.jpg'),
(null,'Phở Xào','Phở xào là món ăn khoái khẩu của không ít gia đình Việt vì cách chế biến đơn giản và dễ biến tấu với nhiều nguyên liệu khác nhau. Bạn cũng có thể tự tay chế biến phở xào tại nhà theo nhiều phong cách khác nhau để làm mới khẩu vị của cả nhà.','https://www.cet.edu.vn/wp-content/uploads/2018/10/pho-xao-mem.jpg'),
(null,'Chân Gà Xào Chua Ngọt và Xào Sả Ớt','Nếu bạn muốn thay đổi thực đơn để kích thích khẩu vị cho cả gia đình thì có thể tham khảo cách làm chân gà xào chua ngọt và chân gà xào sả ớt ngon dưới đây. Với hương vị cay thơm kích thích, hai món ăn này sẽ góp phần làm cho bữa ăn của gia đình bạn thêm ngon miệng.','https://www.cet.edu.vn/wp-content/uploads/2018/10/chan-ga-xao-sa-ot.jpg'),
(null,'Nui Xào Bò Xốt Cà Chua','Nếu bạn không có nhiều thời gian để chế biến một món nước cho bữa sáng hay đơn thuần muốn làm gì đó thật nhanh để thưởng thức khi đói thì các món nui xào là sự gợi ý sáng giá dành cho bạn. Các Đầu Bếp của Cet.edu.vn sẽ hướng dẫn bạn cách làm nui xào bò xốt cà chua và bò trứng thiệt ngon chỉ với vài bước chế biến.','https://www.cet.edu.vn/wp-content/uploads/2018/05/nui-xao.jpg'),
(null,'Mực Khô Xào Chua Ngọt','Nếu bạn yêu thích các món ăn về mực nhưng lại muốn “đổi gió” với một món là lạ nào đó thì trường trung cấp CET xin giới thiệu đến bạn cách làm món mực khô xào chua ngọt. Với cách làm đơn giản, thành phần nguyên liệu lại dễ kiếm thì tại sao bạn lại không thử làm cả gia đình ngạc nhiên với món ăn siêu hấp dẫn này?','https://www.cet.edu.vn/wp-content/uploads/2018/03/muc-kho-la-mon-an-binh-dan.jpg'),
(null,'Mì Gói, Mì Trứng, Mì Ý Xào Hải Sản','Mì là một trong những món ăn gắn bó, gần gũi với người châu Á nói chung và người Việt nói riêng với hàng trăm món mì khác nhau từ nguyên liệu cho đến các cách chế biến. Vì thế, hôm nay hãy cùng với trường trung cấp CET bỏ túi ngay 3 cách làm mì gói, mì trứng, mỳ Ý xào hải sản nhanh gọn, tiện lợi, đơn giản để có thể tự mình làm ngay một món mì ngon ngay tại nhà.','https://www.cet.edu.vn/wp-content/uploads/2018/03/cac-mon-mi-gan-gui-voi-doi-song-nguoi-chau-a.jpg'),
(null,'Mì xào giòn thập cẩm','Mì xào giòn là một món ăn hấp dẫn lại dễ ăn dành cho tất cả mọi đối tượng, không những thế mọi người có thể dễ dàng tìm thấy chúng ở các quán ăn từ bình dân cho đến nhà hàng cao cấp. Nếu đây là món ăn “ruột” của bạn thì bài viết hôm nay là dành cho bạn, chuyên mục học nấu ăn CET sẽ hướng dẫn bạn cách làm món mì xào giòn thập cẩm với nước xốt ngon tại nhà.','https://www.cet.edu.vn/wp-content/uploads/2018/03/mi-xao-gion-thap-cam.jpg'),
(null,'Sườn Xào Chua Ngọt','Sườn heo non xào chua ngọt là món ăn có nhiều cách làm khác nhau và được nhiều gia đình Việt ưa thích trong bữa ăn hằng ngày. Là món ăn xuất hiện trong mâm cơm nhiều gia đình Việt và được yêu thích nhờ vị thịt mềm thơm phức cùng với vị nước xốt chua chua ngọt ngọt ngon, pha chút cay cay, sườn xào chua ngọt khi ăn kèm cơ trắng vô cùng hợp miệng. Sườn xào chua ngọt có nhiều cách làm khác nhau, trong đó hấp dẫn nhất thì có 3 cách làm vừa đơn giản mà hương vị hấp dẫn. 3 cách làm đó là gì chúng ta cùng vào bếp thử tài nghệ nhé.','https://www.cet.edu.vn/wp-content/uploads/2017/12/suon-xao-chua-ngot-mien-nam.jpg');
